/*диапазон цен*/


$(function () {
  $("#price-range-slider").slider({
    range: true,
    min: 120,
    max: 13200,
    values: [1, 13200],
    slide: function slide(event, ui) {
      $("#amount1").val(ui.values[0] + ' грн');
      $("#amount2").val(ui.values[1] + ' грн');
    }
  });
  $("#amount1").val($("#price-range-slider").slider("values", 0));
  $("#amount2").val($("#price-range-slider").slider("values", 1));
});
